body.script:
// 
    // Link Sakti Bos (Pastikan link ini masih aktif di browser)
    const RAW_URL = "https://cdn.discordapp.com/attachments/1468625981584703673/1468929555380043830/monsta_index.json?ex=6985cec1&is=69847d41&hm=dab16bd3285627383c932252854554714c3eeb53acbb89a3ba3a2ab247085464&";

    // Menggunakan jalur proxy yang lebih stabil untuk data JSON mentah
    const getUrl = (url) => `https://api.allorigins.win/raw?url=${encodeURIComponent(url)}`;

    async function init() {
        const grid = document.getElementById('main-grid');
        try {
            const res = await fetch(getUrl(RAW_URL));
            if(!res.ok) throw new Error();
            const data = await res.json();

            grid.innerHTML = data.map(m => `
                <div class="manga-card" onclick="openManga('${m.cdn}')">
                    <img src="${m.cover}" onerror="this.src='https://via.placeholder.com/300x400?text=No+Cover'"/>
                    <div class="card-info">
                        <div class="m-title">${m.title}</div>
                        <div class="m-meta">
                            <span class="m-rating">â˜… ${m.rating}</span>
                            <span style="font-size:9px; color:var(--m-purple); border:1px solid var(--m-purple); padding:1px 4px; font-weight:bold;">${m.type}</span>
                        </div>
                    </div>
                </div>
            `).join('');
        } catch (e) {
            grid.innerHTML = `
                <div style="grid-column:1/-1; text-align:center; padding:50px;">
                    <p style="color:var(--m-purple); font-weight:bold;">SUMMONING FAILED</p>
                    <p style="font-size:12px; color:#666;">Link Discord kedaluwarsa atau Proxy sedang sibuk.</p>
                    <button onclick="location.reload()" style="margin-top:20px; background:var(--m-green); color:#000; border:none; padding:10px 20px; font-weight:bold; cursor:pointer;">TRY AGAIN</button>
                </div>`;
        }
    }

    async function openManga(cdnUrl) {
        document.getElementById('home-view').style.display = 'none';
        document.getElementById('reader-view').style.display = 'block';
        window.scrollTo(0,0);

        const imgArea = document.getElementById('reader-imgs');
        imgArea.innerHTML = "<div class='skeleton' style='height:100vh; width:100%'></div>";

        try {
            const res = await fetch(getUrl(cdnUrl));
            const manga = await res.json();
            document.getElementById('nav-title').innerText = manga.title;

            // Chapter 0 adalah yang terbaru
            const images = manga.chapters[0].images;
            imgArea.innerHTML = images.map(i => `<img src="${i}" loading="lazy"/>`).join('');
        } catch (e) {
            alert("Gagal memuat chapter. Cek link Discord detail komik.");
        }
    }

    window.onload = init;
//
