body.script:
// 
    const INDEX_URL = "https://cdn.discordapp.com/attachments/1468625981584703673/1469168490782003302/monsta_index.json?ex=6986ad48&is=69855bc8&hm=34caabafa1a4bbf700ff4d1f9c7166699d4ae86dac61a91a8dca338472f7e02b&";
    let allData = [];
    let currentManga = null;
    let currentChapIdx = 0;

    // KEMBALI KE METODE ALLORIGINS RAW YANG SUKSES DI V19
    const getUrl = (url) => `https://api.allorigins.win/raw?url=${encodeURIComponent(url)}`;

    async function init() {
        const grid = document.getElementById('main-grid');
        try {
            const res = await fetch(getUrl(INDEX_URL));
            if(!res.ok) throw new Error();
            allData = await res.json();
            renderHome(allData);
        } catch (e) {
            grid.innerHTML = "<div style='grid-column:1/-1; text-align:center; padding:50px; color:var(--m-purple);'>CONNECTION ERROR: Please refresh or check Index link.</div>";
        }
    }

    function renderHome(data) {
        const grid = document.getElementById('main-grid');
        grid.innerHTML = data.map(m => `
            <div class="manga-card" onclick="openDetail('${m.cdn}')">
                <div class="type-tag">${m.type || 'Manga'}</div>
                <img src="${m.cover}" loading="lazy"/>
                <div class="card-info">
                    <div class="card-title">${m.title}</div>
                    <div class="card-meta">
                        <span style="color:var(--m-green)">★ ${m.rating || '0'}</span>
                        <span style="color:var(--m-purple); font-weight:bold;">CH. ${m.last_chapter || '??'}</span>
                    </div>
                </div>
            </div>
        `).join('');
    }

    async function openDetail(cdn) {
        document.getElementById('home-view').style.display = 'none';
        document.getElementById('detail-view').style.display = 'block';
        document.getElementById('reader-view').style.display = 'none';
        window.scrollTo(0,0);

        const detailArea = document.getElementById('detail-content');
        detailArea.innerHTML = "<div class='skeleton' style='height:300px;'></div>";

        try {
            const res = await fetch(getUrl(cdn));
            currentManga = await res.json();

            detailArea.innerHTML = `
                <div class="detail-header">
                    <img class="detail-cover" src="${currentManga.cover}"/>
                    <div class="detail-info">
                        <h1 class="detail-title">${currentManga.title}</h1>
                        <p style="color:var(--text-dim); font-size:14px; line-height:1.6;">${currentManga.metadata.synopsis || 'Monster ini belum memiliki deskripsi.'}</p>
                        <div>${currentManga.metadata.genres.map(g => `<span class="genre-tag">${g}</span>`).join('')}</div>
                        <div style="margin-top:20px;">Status: <span style="color:var(--m-green); font-weight:bold;">${currentManga.metadata.status}</span></div>
                    </div>
                </div>
            `;

            const list = document.getElementById('chapter-list');
            list.innerHTML = currentManga.chapters.map((c, i) => `
                <div class="chap-item" onclick="openReader(${i})">
                    <span>Chapter ${c.chapter_num || (currentManga.chapters.length - i)}</span>
                    <span style="color:var(--m-green); font-size:11px; font-weight:bold;">READ →</span>
                </div>
            `).join('');

            renderRecom();
        } catch (e) { detailArea.innerHTML = "Gagal memuat detail."; }
    }

    function renderRecom() {
        const list = document.getElementById('recom-list');
        const randoms = [...allData].sort(() => 0.5 - Math.random()).slice(0, 10);
        list.innerHTML = randoms.map(r => `
            <div class="recom-item" onclick="openDetail('${r.cdn}')">
                <img src="${r.cover}"/>
                <div>${r.title}</div>
            </div>
        `).join('');
    }

    function openReader(idx) {
        currentChapIdx = idx;
        document.getElementById('detail-view').style.display = 'none';
        document.getElementById('reader-view').style.display = 'block';
        window.scrollTo(0,0);

        const chap = currentManga.chapters[currentChapIdx];

        // Setup Nav
        document.getElementById('chapSelect').innerHTML = currentManga.chapters.map((c, i) => `
            <option value="${i}" ${i === currentChapIdx ? 'selected' : ''}>Ch. ${c.chapter_num}</option>
        `).join('');

        document.getElementById('prevBtn').disabled = (currentChapIdx >= currentManga.chapters.length - 1);
        document.getElementById('nextBtn').disabled = (currentChapIdx <= 0);

        // Render Images
        const imgArea = document.getElementById('reader-imgs');
        imgArea.innerHTML = "<div style='padding:50px; text-align:center;'>Summoning Pages...</div>";
        imgArea.innerHTML = chap.images.map(img => `<img src="${img}" loading="lazy" onerror="this.style.display='none'"/>`).join('');
    }

    function changeChap(dir) {
        let n = currentChapIdx + dir;
        if(n >= 0 && n < currentManga.chapters.length) openReader(n);
    }

    function jumpChap(v) { openReader(parseInt(v)); }

    function backToDetail() {
        document.getElementById('reader-view').style.display = 'none';
        document.getElementById('detail-view').style.display = 'block';
    }

    function searchManga() {
        const q = document.getElementById('searchInput').value.toLowerCase();
        renderHome(allData.filter(m => m.title.toLowerCase().includes(q)));
    }

    window.onload = init;
//
